var LdapAuth = require('./lib/ldapauth');

// ldapsearch -H ldap://x500.bund.de -x -b "ou=BMI,o=Bund,c=DE"

var c = new LdapAuth({
  url: 'ldap://x500.bund.de',
  searchBase: 'ou=BMI,o=Bund,c=DE',
  searchFilter: 'mail={{username}}'
});

c.authenticate('Ulrich.Hertzer@bmi.bund.de', 'test', function(err, user) {
  console.log(err.message, user);
});

c.close();
